import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Item from "./Item"
import Title from "./Title";
import SubTitle from "./SubTitle"
import axios from "axios";

class Cart extends React.Component {
    
    constructor() {
        super();
        this.state = {
            itemList: [],
            userId: window.localStorage.getItem("userId"),
            cartId: window.localStorage.getItem("cartId")
        }
    }

    componentDidMount() {
        //api
        axios
            .get("http://localhost:8080/cart/items/" + this.state.cartId)
            .then(res => {
                if (res.data.success) {
                    this.setState({
                        itemList: res.data.data
                    })
                }
            })
            .catch(err => {
                console.log(err);
            })

    }
    handleRemove = (itemId)=>{
        axios
            .delete("http://localhost:8080/cart/delete",{
                cartId: this.state.cartId,
                itemId: itemId
            })
            .then(res=>{
                if(res.data.success){
                    console.log(res.data.msg);
                }
            })
            .catch(err=>{
                console.log(err);
            })
    }
    findItem(itemId) {

        axios
            .get("http://localhost:8080/items/" + itemId)
            .then(res => {
                if (res.data.success) {
                    console.log(res.data.data);
                    return {
                        price: res.data.data[0].price,
                        name: res.data.data[0].name
                    }
                }
            })
            .catch(err => {
                console.log(err);
            })

    }
    render() {
        return (
            <>
                <Header />
                <div className="container-fluid"
                    style={{
                        backgroundColor: "#efefef",
                        fontFamily: "-apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif",
                        height: "calc(100vh - 80px)"
                    }}>
                    <Title text="CART" />
                    <div style={{ textAlign: "left" }}>
                        <SubTitle text="ITEMS" />
                    </div>

                    <div className="d-flex justify-content-start" style={{ width: "100%", flexDirection: "row" }}>
                        <br></br>
                        {this.state.itemList.map(x=>
                            <Item itemName={x.name} itemPrice={x.price} onClick={()=>this.handleRemove(x.item_id)} value="REMOVE FROM CART"/>
                            )}
                    </div>

                    <div class="d-flex justify-content-center" >
                        <div class="d-flex align-items-center" >

                        </div>
                    </div>
                </div>
                <Footer />
            </>
        );
    }
}

export default Cart;
