import React from "react";
import LargeButton from "./LargeButton";
import Title from "./Title";
import SubTitle from "./SubTitle";
import InputField from "./InputField";
import PhoneImage from "./PhoneImage";
import Footer from "./Footer";
import {Link, Redirect, } from 'react-router-dom';
import "bootstrap";

class Home extends React.Component {
    constructor() {
        super();
        this.state = {
            redirectaj: false
        }
        
        this.handleClickLogIn = this.handleClickLogIn.bind(this);
    }
    handleRedirect = ()=>{
        if(this.state.redirectaj) return <Redirect to="/profile"/>
    }

    handleClickLogIn() {
        //validacije ha hu
        this.props.history.push("/profile");
        this.setState({
            redirectaj: true
        })
    }
    render() {
        return (
        
            <div>
                <div className="container-fluid" >
                    <div className="row justify-content-center">
                        <Title text="#TECH"/>
                    </div>
                    <div className="row justify-content-center">
                        <SubTitle text="New thinking. New #tech."/>
                    </div>
                    <div className="row">
                        <div style={{ visibility: "hidden", fontSize: "30px" }}>d</div>
                    </div>
                    <div className="row">
                        <div className="col-lg-6 col-md-12 col-sm-12 col-xs-12 ">
                            <div className="d-flex justify-content-center" >
                                <PhoneImage style={{ width: "550px", height: "600px" }} />
                            </div>
                        </div>
                        <div className="col-lg-6 col-md-12 col-sm-12 col-xs-12 d-flex flex-row align-items-lg-center align-items-md-stretch">
                            <div className="col align-items-center my-md-5 my-sm-5 my-xs-5">

                                <div className="d-flex justify-content-center" >
                                    <h1 style={{fontSize:"50px",fontWeight:"bold",color:"#666666"}}>W E L C O M E</h1>
                                </div>
                                <div className="d-flex justify-content-center">
                                    <InputField type="text" placeholder="Email"
                                        style={{
                                            marginTop: "10px",
                                            width: "450px",
                                            height: "50px",
                                            color: "#434343",
                                            fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif",
                                            fontEeight: "lighter",
                                            border: "1px solid #dddddd"
                                        }} />
                                </div>
                                <div className="d-flex justify-content-center">
                                    <InputField type="password" placeholder="Password"
                                        style={{
                                            marginTop: "20px",
                                            width: "450px",
                                            height: "50px",
                                            color: "#434343",
                                            fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif",
                                            fontEeight: "lighter",
                                            border: "1px solid #dddddd"
                                        }} />
                                </div>
                                <div className="d-flex justify-content-center">
                                    <Link to="/register" style={{ marginTop: "10px" }}>New user? Register here.</Link>
                                </div>
                                <div className="d-flex justify-content-center">
                                    {this.handleRedirect()}
                                    <LargeButton
                                        value="LOG IN"
                                        style={{
                                            width: "200px",
                                            height: "50px",
                                            borderRadius: "10px",
                                            backgroundColor: "#2d2d2d",
                                            borderColor: "#2d2d2d",
                                            fontSize: "20px",
                                            color: "white"
                                            
                                        }}
                                        handleClick={this.handleClickLogIn}
                                    />
                                </div>
                            </div>
                        </div>
                     </div>
                </div>
                
               <Footer/>
            </div>
            );
    } 
}
export default Home;
