import React from "react";
import "./Item.css";
import LargeButton from "./LargeButton";

function Item(props)
{
    return(
        <div>
            <div id="cont">
                <div id="slika"> <img src={props.src}/></div>
            
                <div id="txtContent">
                    <p>{props.itemName}</p>
                    <p>CATEGORY: {props.itemCategory}</p>
                    <p>PRICE: {props.itemPrice}</p>
                </div>
                
            </div>
            <LargeButton 
                value="ADD TO CART"
                width="100%"
                height="50px"
                fontSize="15px"
            />
        </div>
    );
}

export default Item;
