import React from "react";
class Item extends React.Component {
    constructor(){
        super();
        this.state = {}
    }
    render() {
        return (
            <div class="card mx-2 my-2" style={{ height: "400px", width: "300px", border:"0px"}}>
                {this.props.poruka}
                    <div class="card-body">
                        <img class="rounded-circle" src={this.props.src} style={{ width: "200px", height: "200px" }} />
                        <h3>{this.props.itemName}</h3>
                        <br></br>
                        <p>PRICE: {this.props.itemPrice}</p>
                        <div className="d-flex align-items-end" >
                            <button
                                style={{
                                    width: "100%",
                                    height: "50px",
                                    fontSize: "15px",
                                    color:"white",
                                    backgroundColor: "#6BCDFD",
                                    border:"0px"
                                    
                                }}
                                onClick={this.props.onClick}
                            >{this.props.value}</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Item;
