import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Item from "./Item"
import axios from "axios";

class Shop extends React.Component {

    constructor() {
        super();
        this.state = {
            items: [],
            userId: window.localStorage.getItem("userId"),
            cartId: window.localStorage.getItem("cartId"),
            show: "hidden",
            addedToCart: []
        }
    }

    componentDidMount() {
        axios
            .get("http://localhost:8080/items")
            .then(res => {
                console.log(res.data.success);
                if (res.data.success) {
                    console.log(res.data.data);
                    this.setState({
                        items: res.data.data
                    })
                }
            })
            .catch(err => {
                console.log(err);
            })
    }
    handleChange = (e) => {
        //searchbar

    }
    handleSearch = (keyword) => {
        axios
            .get("http://localhost:8080/search/" + keyword)
            .then(res => {
                if (res.data.success) {

                }
            })
            .catch(err => {
                console.log(err);
            })
    }
    addToCart = (itemId) => {
        axios
            .post("http://localhost:8080/cart/add", {
                cartId: this.state.cartId,
                itemId: itemId
            })
            .then(res => {
                if (res.data.success) {
                    this.setState({
                        show: "visible"
                    })
                    //stavimo u localstorage 
                    if (window.localStorage.getItem("addedToCart") == null) {
                        var addedToCart = [];
                        addedToCart[0] = res.data.data.insertId;
                        localStorage.setItem("addedToCart", addedToCart.toString())
                    }
                    else {
                        //dodamo u niz addedToCart novi id
                        var novi = localStorage.getItem("addedToCart").split(',');
                        novi.push(res.data.data.insertId);
                        localStorage.setItem("addedToCart", novi.toString());
                    }
                }
            })
            .catch(err => {
                console.log(err);
            })
    }

    render() {
        return (
            <>
                <Header />
                <div className="container-fluid"
                    style={{
                        backgroundColor: "#efefef",
                        fontFamily: "-apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif",
                        minHeight: "calc(100vh - 80px)"
                    }}>
                    <div className="d-flex justify-content-center" style={{ width: "100%" }}>
                        <div className="d-flex align-items-center" style={{ flexDirection: "column" }}>
                            <br></br>
                            <form>
                                <input type="text"
                                    style={{
                                        fontSize: "15px",
                                        height: "45px",
                                        width: "500px",
                                        border: "0px",
                                        padding: "10px",
                                        boxSizing: "border-box"
                                    }}
                                    onChange={e => this.handleChange(e)}
                                />
                                <button
                                    style={{
                                        width: "100px",
                                        height: "45px",
                                        fontSize: "15px",
                                        backgroundColor: "#2d2d2d",
                                        border: "0px",
                                        color: "white"
                                    }}>
                                    SEARCH</button>
                            </form>
                        </div>
                    </div>
                    <div className="row mt-5 mx-2">

                        {this.state.items.map(x =>
                            <>
                                <div class="valid-feedback" style={{ visibility: this.props.poruka, backgroundColor: "white", width: "300px" }}>Successfully added to cart.</div>
                                <Item itemName={x.name} itemPrice={x.price + "$"} value="ADD TO CART" onClick={() => this.addToCart(x.item_id)} />
                            </>
                        )}

                    </div>

                </div>
                <Footer />
            </>
        );
    }
}

export default Shop;
