import React from "react";
import LargeButton from "./LargeButton";
class Item extends React.Component {
    render() {
        return (
            <div class="card" style={{ height: "450px", width: "300px" }}>
                <div class="card-body">
                    <div className="d-flex" style={{flexDirection:"column"}}>
                        <img src={this.props.src} style={{ width: "200px", height: "200px" }} />

                        <h3>{this.props.itemName}</h3>
                        <br></br>
                        <p>CATEGORY: {this.props.itemCategory}</p>
                        <p>PRICE: {this.props.itemPrice}</p>
                        <div className="d-flex align-self-end"style={{flexDirection:"column"}} >
                            <LargeButton
                                style={{
                                    width: "100%",
                                    height: "50px",
                                    fontSize: "15px",
                                    backgroundColor: "#6BCDFD"
                                }}
                            />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Item;
