import React from "react";
import "./Item.css";
import LargeButton from "./LargeButton";

function Item(props)
{
    return(
        <div id="main">
            <div id="slika"> <img src={props.src}/></div>
            
            <div id="txtContent">
                    <p>{props.itemName}</p>
                    <p>CATEGORY: {props.itemCategory}</p>
                    <p>PRICE: {props.itemPrice}</p>
                </div>
            <div id="btn">
                <LargeButton 
                    value="ADD TO CART"
                    width="100%"
                    height="50px"
                    fontSize="15px"
                    backgroundColor="6BCDFD"
                />
            </div>
           
        </div>
    );
}

export default Item;
