import React from "react";
import LargeButton from "./LargeButton";

class Item extends React.Component {
    render() {
        return (
            <div id="main">
                <div id="cont">
                    <div id="slika"> <img src={props.src} /></div>

                    <div id="txtContent">
                        <h3>{props.itemName}</h3>
                        <p>CATEGORY: {props.itemCategory}</p>
                        <p>PRICE: {props.itemPrice}</p>
                    </div>
                </div>
                <div id="btn">
                    <LargeButton
                        value="ADD TO CART"
                        width="100%"
                        height="50px"
                        fontSize="15px"
                        backgroundColor="#6BCDFD"
                    />
                </div>

            </div>
        );
    }
}

export default Item;
