import React from "react";
import "./SearchBar.css";
import InputField from "./InputField";
import LargeButton from "./LargeButton";
function SearchBar()
{
    const font="15px";
    const visina="40px";
    const boja="#2d2d2d";
    //font size and height need to be the same if you want to align the input and button horizontally
    //also box-sizing needs to be the same on both inputs
    return(
        <div id="searchCont">
            <InputField 
                type="text"
                width="500px"
                height={visina}
                fontSize={font}
            />
            <LargeButton
                width="100px"
                height={visina}
                fontSize={font}
                value="SEARCH"
                backgroundColor={boja}
                borderColor={boja}
            />
        </div>

    );
}

export default SearchBar;
