import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Item from "./Item"
import SearchBar from "./SearchBar";
import axios from "axios";

class Shop extends React.Component {

    constructor() {
        super();
        this.state = {
            items: [],
            userId: window.localStorage.getItem("userId"),
            cartId: window.localStorage.getItem("cartId"),
            show:"none"
        }
    }

    componentDidMount() {
        axios
            .get("http://localhost:8080/items")
            .then(res => {
                console.log(res.data.success);
                if (res.data.success) {
                    console.log(res.data.data);
                    this.setState({
                        items: res.data.data
                    })
                }
            })
            .catch(err => {
                console.log(err);
            })
    }
    handleChange = () => {

    }
    addToCart = (itemId) => {
        axios
            .post("http://localhost:8080/cart/add", {
                cartId: this.state.cartId,
                itemId: itemId
            })
            .then(res => {
                if (res.data.success) {
                    this.setState({
                        show:"display-block"
                    })
                }
            })
            .catch(err=>{
                console.log(err);
            })
    }
    handleShowSuccess = () => {
        return (<div class="alert alert-success" style={{display:this.state.show}}>
            <strong>Successfully added to cart!</strong> You can now proceed to checkout.
      </div>);
    }
    render() {
        return (
            <>
                <Header />
                <div className="container-fluid"
                    style={{
                        backgroundColor: "#efefef",
                        fontFamily: "-apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif",
                        minHeight: "calc(100vh - 80px)"
                    }}>
                    <div className="d-flex justify-content-center" style={{ width: "100%" }}>
                        <div className="d-flex align-items-center" style={{ flexDirection: "column" }}>
                            <br></br>
                            <SearchBar onChange={this.handleChange} />
                        </div>
                    </div>
                    <div className="row mt-5 mx-2">
                        {this.state.items.map(x =>
                            <Item itemName={x.name} itemPrice={x.price + "$"} value="ADD TO CART" poruka={()=>this.handleShowSuccess()} onClick={() => this.addToCart(x.item_id)} />
                        )}

                    </div>

                </div>
                <Footer />
            </>
        );
    }
}

export default Shop;
