import React from "react";
class Item extends React.Component {
    constructor() {
        super();
    }
    render() {
        return (
            <div class="card mx-2 my-2" style={{ border: "0px" }}>

                <div class="card-body">
                    <div class="d-flex justify-content-between" style={{ flexDirection: "column" }}>
                        <div className="d-flex justify-content-start">
                            <img src={this.props.src} style={{ width: "200px", maxHeight: "200px" }} />
                        </div>

                        <div className="d-flex justify-content-end" style={{ minHeight: "200px", width: "300px", backgroundColor: "blue" }}>
                            <h3>{this.props.itemName}</h3>
                            <br></br>
                            <p>PRICE: {this.props.itemPrice}</p>
                            <br></br>

                            <div className="d-flex align-items-end" >
                                <button
                                    style={{
                                        width: "100%",
                                        height: "50px",
                                        fontSize: "15px",
                                        color: "white",
                                        backgroundColor: "#6BCDFD",
                                        border: "0px"

                                    }}
                                    onClick={this.props.onClick}
                                >{this.props.value}</button>
                            </div>
                        </div>

                    </div>

                </div>
            </div>
        );
    }
}

export default Item;
