import React from "react";
import FooterHeaderBtns from "./FooterHeaderBtns";
import { Redirect } from "react-router-dom";

class Header extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            redirec: false
        }
    }
    handleClick = (ruta) => {
        console.log(window.location);
        window.location.replace(window.location.origin);
        // this.setState({
        //     redirec: true
        // })
        
    }
    handleRedirect = (ruta) => {
        if (this.state.redirec) return <Redirect to={ruta} />
    }
    render() {
        return (
            <header>
                <div className="d-flex justify-content-between"
                    style={{
                        backgroundColor: "#2d2d2d",
                        width: "100%",
                        height: "80px"
                    }}>
                    <div className="col d-flex justify-content-start align-items-center">
                        {() => {
                            this.handleRedirect("/")
                        }}
                        <FooterHeaderBtns value="#TECH" onClick={() => {
                            this.handleClick("/")
                        }} />
                    </div>
                    <div className="col d-flex justify-content-end align-items-center">
                        <FooterHeaderBtns value="PROFILE" />
                        <FooterHeaderBtns value="CART" />
                        <FooterHeaderBtns value="SHOP" />
                        <FooterHeaderBtns value="LOG OUT" />
                    </div>

                </div>
            </header>
        );
    }
}

export default Header;
